UPDATE TB_CHALLENGE SET VIEW_COUNT = VIEW_COUNT + 1 WHERE ACTIVITY_NO = #{ activityNo }
SELECT USER_ID FROM TB_USER JOIN TB_CHALLENGE USING(USER_NO) WHERE ACTIVITY_NO = #{ activityNo }
SELECT CATEGORY_NAME FROM TB_CATEGORY JOIN TB_ACTIVITY USING (CATEGORY_NO) WHERE ACTIVITY_NO = #{ activityNo }
SELECT ACTIVITY_NO, CHALLENGE_TITLE, CHALLENGE_CONTENT, CHALLENGE_PLACE, ACHIEVEMENT_COUNT, SUCCESS_LIMIT, MIN_PARTICIPANT, MAX_PARTICIPANT, STATUS, VIEW_COUNT,
ORIGIN_NAME, CHANGE_NAME, UPLOAD_DATE, MODIFY_DATE, START_DATE, END_DATE,
USER_NO, CATEGORY_NO FROM TB_CHALLENGE JOIN TB_ACTIVITY USING(ACTIVITY_NO) WHERE ACTIVITY_NO = #{ activityNo }
SELECT COUNT(*) FROM TB_LIKE_CHALLENGE WHERE ACTIVITY_NO = #{ activityNo } AND USER_NO = #{ userNo }
SELECT USER_NO FROM TB_LIKE_CHALLENGE WHERE ACTIVITY_NO = #{ activityNo } AND USER_NO = #{ userNo }
SELECT COUNT(*) FROM TB_LIKE_CHALLENGE WHERE ACTIVITY_NO = #{ activityNo }
INSERT INTO TB_LIKE_CHALLENGE VALUES ( #{ activityNo }, #{ userNo }, SYSDATE )
DELETE FROM TB_LIKE_CHALLENGE WHERE ACTIVITY_NO = #{ activityNo } AND USER_NO = #{ userNo }
INSERT ALL INTO TB_CHALLENGE( ACTIVITY_NO, CHALLENGE_TITLE, CHALLENGE_CONTENT, CHALLENGE_PLACE, ACHIEVEMENT_COUNT, SUCCESS_LIMIT, MIN_PARTICIPANT, MAX_PARTICIPANT, START_DATE, END_DATE, USER_NO ) VALUES( SEQ_ACTIVITY_NO.NEXTVAL, #{challengeTitle}, #{challengeContent}, #{challengePlace}, #{achievementCount}, #{successLimit}, #{minParticipant}, #{maxParticipant}, #{startDate}, #{endDate}, #{userNo} ) INTO TB_ACTIVITY( ACTIVITY_NO, ORIGIN_NAME, CHANGE_NAME, UPLOAD_DATE, MODIFY_DATE, CATEGORY_NO, STATUS, TYPE ) VALUES( SEQ_ACTIVITY_NO.CURRVAL, #{originName}, #{changeName}, SYSDATE, SYSDATE, #{categoryNo}, DEFAULT, 2 ) SELECT * FROM DUAL
UPDATE TB_CHALLENGE SET CHALLENGE_TITLE = #{challengeTitle}, CHALLENGE_CONTENT = #{challengeContent}, CHALLENGE_PLACE = #{challengePlace}, ACHIEVEMENT_COUNT=#{achievementCount}, SUCCESS_LIMIT = #{successLimit}, MIN_PARTICIPANT=#{minParticipant}, MAX_PARTICIPANT=#{maxParticipant}, START_DATE = #{startDate}, END_DATE = #{endDate} WHERE ACTIVITY_NO = #{ activityNo }
UPDATE TB_ACTIVITY SET ORIGIN_NAME = #{originName}, CHANGE_NAME = #{changeName}, MODIFY_DATE = SYSDATE, CATEGORY_NO = #{categoryNo} WHERE ACTIVITY_NO = #{ activityNo }
UPDATE TB_ACTIVITY SET STATUS = 'N' WHERE ACTIVITY_NO = #{ activityNo }
