INSERT ALL INTO TB_EVENT ( ACTIVITY_NO, EVENT_TITLE, EVENT_CONTENT, EVENT_PLACE, EVENT_DATE, PARTICIPANTS ) VALUES ( SEQ_ACTIVITY_NO.NEXTVAL, #{ eventTitle }, #{ eventContent }, #{ eventPlace }, #{ eventDate}, DEFAULT ) INTO TB_ACTIVITY( ACTIVITY_NO, UPLOAD_DATE, CHANGE_NAME, CATEGORY_NO, TYPE ) VALUES ( SEQ_ACTIVITY_NO.CURRVAL, SYSDATE, #{ changeName }, #{ categoryNo }, 1 ) SELECT * FROM DUAL
